    def on_attributes_find(xf):
        sndr = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sndr.connect(("localhost", 9996))
        sndr.send("get_username?|{}".format(xf).encode())
        uname, email = sndr.recv(1024).decode().split("|")
        sndr.close()
        if uname != "error_username_fail":
            self.username_label.setText(uname)
        else:
            self.username_label.setText("error")


    def on_attrs_find(xf):
        sndr = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sndr.connect(("localhost", 9996))
        sndr.send("get_profile_attrs?|{}".format(xf).encode())
        status, badges, about = sndr.recv(1024).decode().split("|")
        sndr.close()

        if status != "None":
            self.no_status_label.setText(status)
        if badges != "None":
            dev = False
            mod = False
            sub = False
            transfered = False
            badges_raw = list(badges.split(";"))
            if "dev" in badges_raw:
                dev = True
            if "mod" in badges_raw:
                mod = True
            if "sub" in badges_raw:
                sub = True
            if "transfered" in badges_raw:
                transfered = True
        if about != "None":
            self.about_label.setText(about)

    on_attributes_find(xf=xf)

    on_attrs_find(xf)

